import React from "react";
import moment from 'moment';
import {Link} from "react-router-dom";

export default class Calendar extends React.Component {


    WeekDayShort = moment.weekdaysShort();
    WeekDayShortName = this.WeekDayShort.map(day => {
        return (
            <th key={day} className="week-day">
                {day}
            </th>
        );
    });

    month = () => {
        return moment().format("MMMM");
    }

    prevMon

    year = () => {
        return moment().format("YYYY");
    }

    state = {
        stateObject: moment(),
        allMonths: moment.months()
    }

    render() {

        const currentDate = moment();
        const currentDay = () => {
            return currentDate.format("DD");
        }

        const firstDayOfMonth = () => {
            return currentDate.startOf("month").format("d");
        };

        let blanksBegin = [];
        for (let i = 0; i < firstDayOfMonth(); i++) {
            blanksBegin.push(
                <td key={i} className="calendar-day">{""}</td>
            );
        }

        let daysInMonth = [];
        for (let day = 1; day <= currentDate.daysInMonth(); day++) {
            let today = day == moment().format("D") ? "Today" : "";
            daysInMonth.push(
                <td key={day} className={`calendar-day${today}`}>
                    <Link to="/day">
                        <button>
                            <div className="date-number">
                                {day}
                            </div>
                            <div className="tasks-number">
                                tasks
                            </div>
                        </button>
                    </Link>
                </td>
            )
        }

        const totalCells = 7 * Math.ceil((parseInt(firstDayOfMonth()) + parseInt(daysInMonth.length)) / 7);
        const blanksEnd = [];
        for (let i = 0; i < totalCells - (blanksBegin.length + daysInMonth.length); i++) {
            blanksEnd.push(
                <td key={i + daysInMonth.length + blanksBegin.length} className="calendar-day">{""}</td>
            );
        }

        const allDays = [...blanksBegin, ...daysInMonth, ...blanksEnd];
        let rows = [];
        let cells = [];

        allDays.forEach(
            (row, i) => {
                if (i % 7 !== 0) {
                    cells.push(row);
                } else {
                    rows.push(cells);
                    cells = [];
                    cells.push(row);
                }
                if (i === allDays.length - 1) {
                    rows.push(cells);
                }
            }
        );

        if (rows[0].length === 0) {
            rows.splice(0, 1);
        }

        let daysWrapped = rows.map ((day) => {
            return <tr className="day-row">{day}</tr>
        });

        return (
            <div className="calendar-container">
                <table className="calendar">
                    <thead className="calendar-header">
                    <h2>Calendar</h2>
                    <div className="calendar-navi">
                        <div className="calendar-navi-month">
                            <button onClick={prevMonth}>{"<"}</button>
                            <button>
                                {this.month()}
                            </button>
                            <button>{">"}</button>
                        </div>
                        <div className="calendar-navi-year">
                            <button>{"<"}</button>
                            <button>
                                {this.year()}
                            </button>
                            <button>{">"}</button>
                        </div>
                    </div>
                    <tr className="week-days">{this.WeekDayShortName}</tr>
                    </thead>
                    <tbody className="calendar-body">
                    {daysWrapped}
                    </tbody>
                </table>
            </div>
        );
    }
}
