import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

export default function Day() {
  const { absoluteDate } = useParams();
  const [backendData, setBackendData] = useState([]);
  const [formData, setFormData] = useState({
    //date: absoluteDate,
    name: "",
    description: "",
  });

  useEffect(() => {
    fetch(`/day/${absoluteDate}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        // Handle the response data
        console.log("API Response:", data);
        setBackendData(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, [absoluteDate, backendData]);

  const handleChange = (event) => {
    const { name, value } = event.target;
    if (name === "name") {
      setFormData((prevFormData) => ({
        ...prevFormData,
        name: value,
      }));
    } else if (name === "description") {
      setFormData((prevFormData) => ({
        ...prevFormData,
        description: value,
      }));
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    fetch(`/day/addTask/${absoluteDate}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        absoluteDate,
        name: formData.name,
        description: formData.description,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response not ok!");
        }
        return response.json();
      })
      .then((data) => {
        console.log("Data saved: ", data);
      })
      .catch((error) => {
        console.error("Error: ", error);
      });
  };

  const updateClick = () => {};
  const handleUpdate = () => {};

  // const handleDelete = (event) => {
  //   event.preventDefault();
  // };

  return (
    <div className="day-page">
      <h1>Day Page</h1>
      {typeof backendData === "undefined" ? (
        <p>Loading...</p>
      ) : (
        backendData.map((task) => (
          <div className="task" key={task._id}>
            <h1>{task.name}</h1>
            <p>{task.description}</p>
            <button onClick={handleUpdate} className="updateButton">
              Update
            </button>
            <button
              onClick={(event) => {
                event.preventDefault();
                fetch(`/day/deleteTask/${task._id}`, {
                  method: "DELETE",
                  headers: {
                    "Content-Type": "application/json",
                  },
                  body: JSON.stringify({
                    absoluteDate,
                    name: formData.name,
                    description: formData.description,
                  }),
                })
                  .then((response) => {
                    if (!response.ok) {
                      throw new Error("Network response not ok!");
                    }
                    return response.json();
                  })
                  .then((data) => {
                    console.log("Data saved: ", data);
                  })
                  .catch((error) => {
                    console.error("Error: ", error);
                  });
              }}
              className="deleteButton"
            >
              Delete
            </button>
          </div>
        ))
      )}

      <div>
        <form className="taskInput" onSubmit={handleSubmit}>
          <label>Date: {absoluteDate}</label>
          <label>
            Name:
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </label>
          <label>
            Description:
            <textarea
              type="text"
              name="description"
              value={formData.description}
              onChange={handleChange}
            />
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>

      <Link to="/">
        <button className="button">Back</button>
      </Link>
    </div>
  );
}
